mapper.insert:
			INSERT INTO FILE_COMMUNITY
            (
                          FILE_NO
                        , NICKNAME
                        , NOTICE_CATEGORY
                        , NOTICE_NO
                        , NOTICE_FILE_NAME
                        , NOTICE_FILE_PATH
                        , NOTICE_FILE_SIZE
                        , COMMUNITY_IMG_URL
            ) value (
            	  (SELECT 
                   		IFNULL(MAX(FILE_NO), 0) +1
                   FROM   
                   		FILE_COMMUNITY ALIAS_FOR_SUBQUERY
                   WHERE  
                   		NOTICE_NO = #{noticeNo}
                   AND    
                   		NOTICE_CATEGORY = #{noticeCategory})
		            , #{nickname}
		            , #{noticeCategory}
		            , #{noticeNo}
		            , #{noticeFileName}
		            , #{noticeFilePath}
		            , #{noticeFileSize}
		            , #{communityImgUrl}
            )

mapper.select:
SELECT * FROM FILE_COMMUNITY
WHERE 
	NOTICE_CATEGORY = #{noticeCategory}
AND
     NOTICE_NO = #{noticeNo}

mapper.delete:
DELETE FROM FILE_COMMUNITY
WHERE 
	NOTICE_CATEGORY = #{noticeCategory}
AND 
	NOTICE_NO = #{noticeNo}
AND
	FILE_NO = #{fileNo}

DELETE FROM FILE_COMMUNITY
WHERE
    NOTICE_NO = #{noticeNo}
AND 
    NOTICE_CATEGORY = #{noticeCategory}
