mapper.insert:
INSERT INTO file_customerservice
(
FILE_NO
, QNA_NO
, ID
, QNA_FILENAME
, QNA_FILE_PATH
, QNA_FILE_SIZE
, NICKNAME
, QNA_IMG_URL

) values (
(SELECT
IFNULL(MAX(FILE_NO), 0) + 1
FROM
file_customerservice ALIAS_FOR_SUBQUERY
WHERE QNA_NO = #{qnaNo})
, #{qnaNo}
, #{id}
, #{qnaFileName}
, #{qnaFilePath}
, #{qnaFileSize}
, #{nickname}
, #{qnaImgUrl}
)

mapper.delete:
        DELETE FROM file_customerservice
        WHERE QNA_NO = #{qnaNo}
        AND FILE_NO = #{fileNo}
		AND ID = #{id}

mapper.select:
SELECT * FROM file_customerservice
WHERE QNA_NO = #{qnaNo}
AND ID = #{id}
