mapper.insert:
INSERT INTO file_myhouse
(
FILE_NO
, HOUSE_NO
, MYHOUSE_NO
, MYHOUSE_CATEGORY
, NICKNAME
, MYHOUSE_FILENAME
, MYHOUSE_FILE_PATH
, MYHOUSE_FILE_SIZE
, MYHOUSE_IMG_URL

) values (
(SELECT
IFNULL(MAX(FILE_NO), 0) + 1
FROM
file_myhouse ALIAS_FOR_SUBQUERY
WHERE
MYHOUSE_CATEGORY = #{myhouseCategory}
AND
HOUSE_NO = #{houseNo}
AND
MYHOUSE_NO = #{myhouseNo})
, #{houseNo}
, #{myhouseNo}
, #{myhouseCategory}
, #{nickname}
, #{myhouseFilename}
, #{myhouseFilePath}
, #{myhouseFileSize}
, #{myhouseImgUrl}
)

mapper.delete:
DELETE FROM file_myhouse
WHERE myhouse_no = #{myhouseNo}
AND MYHOUSE_CATEGORY = #{myhouseCategory}
AND HOUSE_NO = #{houseNo}
AND FILE_NO = #{fileNo}

mapper.select:
SELECT * FROM file_myhouse
WHERE myhouse_no = #{myhouseNo}
  AND MYHOUSE_CATEGORY = #{myhouseCategory}
  AND HOUSE_NO = #{houseNo}

mapper.delete:
	DELETE FROM file_myhouse
     WHERE myhouse_no = #{myhouseNo}
     AND MYHOUSE_CATEGORY = #{myhouseCategory}
     AND HOUSE_NO = #{houseNo}
