mapper.update:
			UPDATE NOTICE_MYHOUSE 
			SET myhouse_hit = myhouse_hit + 1 
			WHERE myhouse_no = #{myhouseNo}
        	AND myhouse_category = #{myhouseCategory}
        	AND HOUSE_NO = #{houseNo}

mapper.select:
