mapper.insert:
			INSERT INTO projectdb.NOTICE_COMMUNITY 
		(
			 NOTICE_NO
			, NOTICE_CATEGORY
			, NICKNAME
			, NOTICE_REGDATE
			, NOTICE_TITLE
			, NOTICE_CONTENT
			, NOTICE_HIT

    		) values (
    			(SELECT 
					IFNULL(MAX(NOTICE_NO), 0) + 1 
				FROM 
					projectdb.NOTICE_COMMUNITY ALIAS_FOR_SUBQUERY 
				WHERE 
					NOTICE_CATEGORY = #{noticeCategory})
									, #{noticeCategory}
									, #{nickname}
									, date_format(now(), '%Y-%m-%d %H:%m')
									, #{noticeTitle}
									, #{noticeContent}
									, #{noticeHit}
   		 )

mapper.update:
UPDATE NOTICE_COMMUNITY
SET
NOTICE_CATEGORY = #{noticeCategory}
	, NOTICE_TITLE = #{noticeTitle}
	, NOTICE_CONTENT = #{noticeContent}
WHERE
	NOTICE_CATEGORY = #{noticeCategory}
AND
	NOTICE_NO = #{noticeNo}

mapper.select:
SELECT * FROM NOTICE_COMMUNITY
WHERE
	NOTICE_CATEGORY = #{noticeCategory}
AND
	NOTICE_NO = #{noticeNo}

mapper.delete:
DELETE FROM NOTICE_COMMUNITY
WHERE 
	NOTICE_NO = #{noticeNo} 
AND 
	NOTICE_CATEGORY = #{noticeCategory}

		      DELETE FROM COMMUNITY_COMMENT
              WHERE
                   NOTICE_NO = #{noticeNo}
              AND 
                  NOTICE_CATEGORY = #{noticeCategory}

mapper.select:
SELECT
FROM NOTICE_COMMUNITY WHERE NOTICE_CATEGORY = #{communityList.noticeCategory}
AND NOTICE_TITLE LIKE CONCAT('%', #{communityList.searchKeyword}, '%')
AND NOTICE_CONTENT LIKE CONCAT('%', #{communityList.searchKeyword}, '%')
AND NICKNAME LIKE CONCAT('%', #{communityList.searchKeyword}, '%')
ORDER BY NOTICE_NO DESC LIMIT #{criteria.amount} OFFSET #{criteria.startNum}

mapper.update:
UPDATE like
SET LIKE_NO = LIKE_NO + 1 
AND LIKE_HIT = LIKE_HIT + 1
WHERE NOTICE_CATEGORY=#{noticeCategory} 
AND NOTICE_NO=#{noticeNo}
AND LIKE_NO= 0
AND NICKNAME=#{nickname}

UPDATE like
SET LIKE_NO = 0 
AND LIKE_HIT = LIKE_HIT - 1
WHERE NOTICE_CATEGORY=#{noticeCategory} 
AND NOTICE_NO=#{noticeNo}
AND LIKE_NO= 1
AND NICKNAME=#{nickname}
